@charset "UTF-8";

@import "settings";
@import "layout";
@import "fonts";
@import "icons";
@import "cards";

body {
  background: url("../images/bg.jpg");
  background-color: #041229;
  background-size: cover;
  background-repeat: no-repeat;
  background-attachment: fixed;
  margin: 0;
  text-align: center;
}
#intro-wrapper {
  text-shadow: 0px 0px 10px #000;
  h1,
  h2,
  .links {
    transition: opacity 0.5s, transform 0.5s;
    color: #eee;
    opacity: 1;
  }

  @media (max-width: 600px) {
    h1 {
      font-size: $h1-font-size * 1.8;
    }
    h2 {
      font-size: $h2-font-size * 1.2;
    }
  }
  @media (max-width: 350px) {
    h1 {
      font-size: $h1-font-size * 0.9;
    }
    h2 {
      font-size: $h2-font-size * 0.7;
    }
  }
  .links {
    text-align: center;
    margin: 20px auto 0;
    max-width: 800px;

    a {
      display: inline-block;
      font-family: $sans-serif;
      color: #ddd;
      margin-right: 20px;
      transition: color 0.4s, box-shadow 0.4s;
    }
    a.link-cvr {
      margin-right: 0px;
    }
    @media (max-width: 350px) {
      a {
        margin-top: 5px;
        margin-right: none;
        display: block;
        font-size: $base-font-size * 0.9;
      }
    }

    a:hover {
      text-shadow: 0px 0px 15px #000;
      color: #fff;
      //transform: perspective(1px) scale3d(1.1, 1.1, 1.1) translateZ(0);
    }
  }
}
body.welcome-hidden #intro-wrapper {
  h1,
  h2,
  .links {
    opacity: 0;
    transform: translateY(30px);
  }
}

#contents-wrapper {
  font-size: $base-font-size * 1.5;
  line-height: $base-font-size * 1.25;
  @media (max-width: 350px) {
    font-size: $base-font-size * 1.2;
  }
}

#contents-wrapper .button {
  width: 100%;
  margin-top: 20px;
  text-align: center;
  font-size: $base-font-size;
  transition: background-color 0.5s;
  display: block;
  padding: 10px;
  background: rgba(99, 11, 17, 0.8);
  border: 1px solid #000;
  color: #fff;
  &:hover {
    background: rgba(99, 11, 17, 0.95);
  }
}

body.welcome-hidden #intro-wrapper::after {
  display: none;
}

#intro-wrapper::after {
  display: block;
  font-size: 70px;
  content: "﹀";
  position: absolute;
  width: 120px;
  left: 50vw;
  margin-left: -60px;
  bottom: 50px;
  animation: pulsate 1s linear 0s infinite alternate;
}
@media only screen and (max-height: 390px) {
  #intro-wrapper::after {
    display: none;
  }
}
@media only screen and (min-width: 600px) and (max-height: 570px) {
  #intro-wrapper::after {
    display: none;
  }
}

@keyframes pulsate {
  from {
    opacity: 0.2;
  }
  to {
    opacity: 0.8;
  }
}
